import React, {useState} from 'react';


function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchComicVinesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('https://comicvine.gamespot.com/api/issues/?api_key=82c0a7bade3e1084572773a68abe4ba7e4d21a76&format=json');
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();
      console.log(data)

      const transformedComics = data.results.map((comicData) => {
        return {
          id: comicData.id,
          image: comicData.image.original_url,
          dateAdded: comicData.date_added,
          name: comicData.name,
          issueNumber: comicData.issue_number
        };
      });
      setMovies(transformedComics);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchComicVinesHandler();
  }, [fetchComicVinesHandler]);



  return (
    <div>

    </div>
  );
}

export default App;
